import React, { useState, useEffect } from 'react';
import {
  DndContext,
  DragEndEvent,
  DragOverEvent,
  DragStartEvent,
  PointerSensor,
  useSensor,
  useSensors,
  closestCorners,
  DragOverlay,
} from '@dnd-kit/core';
import {
  SortableContext,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { Column } from './Column';
import { TaskCard } from './TaskCard';
import { TaskModal } from './TaskModal';
import { NotificationSystem } from './NotificationSystem';
import { Task, ColumnType, ProjectStats } from '../types';
import { taskOperations, columnOperations, projectOperations } from '../firebase/config';
import { Plus, Settings, BarChart3, Filter, Search, Download } from 'lucide-react';

interface KanbanBoardProps {
  projectId?: string;
  isMobileView?: boolean;
  isOffline?: boolean;
}

const KanbanBoard: React.FC<KanbanBoardProps> = ({
  projectId = 'default',
  isMobileView = false,
  isOffline = false,
}) => {
  // StateÁÆ°ÁêÜ
  const [columns, setColumns] = useState<ColumnType[]>([]);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [activeTask, setActiveTask] = useState<Task | null>(null);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [projectStats, setProjectStats] = useState<ProjectStats | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterPriority, setFilterPriority] = useState<string>('all');
  const [notifications, setNotifications] = useState<any[]>([]);

  // „Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Çª„É≥„Çµ„Éº
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    })
  );

  // ÂàùÊúü„Éá„Éº„ÇøË™≠„ÅøËæº„Åø
  useEffect(() => {
    loadKanbanData();
  }, [projectId]);

  // „É™„Ç¢„É´„Çø„Ç§„É†„É™„Çπ„Éä„ÉºË®≠ÂÆö
  useEffect(() => {
    if (isOffline) return;

    const unsubscribeTasks = taskOperations.onTasksChange((updatedTasks) => {
      setTasks(updatedTasks);
      updateProjectStats(updatedTasks);
    });

    const unsubscribeColumns = columnOperations.onColumnsChange((updatedColumns) => {
      setColumns(updatedColumns);
    });

    return () => {
      unsubscribeTasks();
      unsubscribeColumns();
    };
  }, [isOffline]);

  const loadKanbanData = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const [loadedTasks, loadedColumns] = await Promise.all([
        projectId === 'default' 
          ? taskOperations.getAllTasks()
          : taskOperations.getTasksByProject(projectId),
        columnOperations.getAllColumns(),
      ]);

      setTasks(loadedTasks);
      setColumns(loadedColumns);

      // „Éó„É≠„Ç∏„Çß„ÇØ„ÉàÁµ±Ë®à„ÇíÊõ¥Êñ∞
      await updateProjectStats(loadedTasks);

    } catch (err) {
      console.error('„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      setError('„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setIsLoading(false);
    }
  };

  const updateProjectStats = async (currentTasks: Task[]) => {
    try {
      const stats = await projectOperations.getProjectStats(
        projectId === 'default' ? undefined : projectId
      );
      setProjectStats(stats);
    } catch (err) {
      console.error('Áµ±Ë®à„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
    }
  };

  const addNotification = (notification: {
    type: 'success' | 'error' | 'warning' | 'info';
    title: string;
    message: string;
  }) => {
    const newNotification = {
      id: Date.now().toString(),
      ...notification,
      timestamp: new Date(),
    };
    setNotifications(prev => [...prev, newNotification]);

    // 5ÁßíÂæå„Å´Ëá™ÂãïÂâäÈô§
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== newNotification.id));
    }, 5000);
  };

  // „Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Ç§„Éô„É≥„Éà
  const handleDragStart = (event: DragStartEvent) => {
    const { active } = event;
    const task = tasks.find(t => t.id === active.id);
    setActiveTask(task || null);
  };

  const handleDragOver = (event: DragOverEvent) => {
    const { active, over } = event;
    if (!over) return;

    const activeId = active.id as string;
    const overId = over.id as string;

    const activeTask = tasks.find(t => t.id === activeId);
    if (!activeTask) return;

    const overColumn = columns.find(c => c.id === overId);
    if (overColumn && activeTask.columnId !== overId) {
      setTasks(prev =>
        prev.map(task =>
          task.id === activeId
            ? { ...task, columnId: overId, updatedAt: new Date() }
            : task
        )
      );
    }
  };

  const handleDragEnd = async (event: DragEndEvent) => {
    const { active, over } = event;
    setActiveTask(null);

    if (!over) return;

    const activeId = active.id as string;
    const overId = over.id as string;

    const activeTask = tasks.find(t => t.id === activeId);
    if (!activeTask) return;

    const overColumn = columns.find(c => c.id === overId);
    if (overColumn && activeTask.columnId !== overId) {
      try {
        await taskOperations.moveTask(activeId, overId);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÁßªÂãï„Åó„Åæ„Åó„Åü',
          message: `„Äå${activeTask.title}„Äç„Çí„Äå${overColumn.title}„Äç„Å´ÁßªÂãï„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Çø„Çπ„ÇØ„ÅÆÁßªÂãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Çø„Çπ„ÇØ„ÅÆÁßªÂãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
        // „Ç®„É©„ÉºÊôÇ„ÅØÂÖÉ„ÅÆÁä∂ÊÖã„Å´Êàª„Åô
        loadKanbanData();
      }
    }
  };

  // „Çø„Çπ„ÇØÊìç‰Ωú
  const handleAddTask = (columnId: string) => {
    setEditingTask(null);
    setIsTaskModalOpen(true);
  };

  const handleEditTask = (task: Task) => {
    setEditingTask(task);
    setIsTaskModalOpen(true);
  };

  const handleSaveTask = async (taskData: Omit<Task, 'id' | 'createdAt' | 'updatedAt'>) => {
    try {
      if (editingTask) {
        await taskOperations.updateTask(editingTask.id, taskData);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÊõ¥Êñ∞„Åó„Åæ„Åó„Åü',
          message: `„Äå${taskData.title}„Äç„ÇíÊõ¥Êñ∞„Åó„Åæ„Åó„Åü`,
        });
      } else {
        const taskId = await taskOperations.addTask({
          ...taskData,
          projectId: projectId === 'default' ? undefined : projectId,
        });
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„Çí‰ΩúÊàê„Åó„Åæ„Åó„Åü',
          message: `„Äå${taskData.title}„Äç„Çí‰ΩúÊàê„Åó„Åæ„Åó„Åü`,
        });
      }

      setIsTaskModalOpen(false);
      setEditingTask(null);
    } catch (err) {
      console.error('„Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      addNotification({
        type: 'error',
        title: '„Ç®„É©„Éº',
        message: '„Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
      });
    }
  };

  const handleDeleteTask = async (taskId: string) => {
    const task = tasks.find(t => t.id === taskId);
    if (!task) return;

    if (window.confirm(`„Äå${task.title}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü`)) {
      try {
        await taskOperations.deleteTask(taskId);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü',
          message: `„Äå${task.title}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Çø„Çπ„ÇØ„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Çø„Çπ„ÇØ„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
      }
    }
  };

  const handleAddColumn = async () => {
    const title = window.prompt('Êñ∞„Åó„ÅÑ„Ç´„É©„É†Âêç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ:');
    if (title) {
      try {
        await columnOperations.addColumn({
          title,
          color: '#6b7280',
          order: columns.length,
          taskIds: [],
        });
        addNotification({
          type: 'success',
          title: '„Ç´„É©„É†„ÇíËøΩÂä†„Åó„Åæ„Åó„Åü',
          message: `„Äå${title}„Äç„Ç´„É©„É†„ÇíËøΩÂä†„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Ç´„É©„É†„ÅÆËøΩÂä†„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Ç´„É©„É†„ÅÆËøΩÂä†„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
      }
    }
  };

  // „Éï„Ç£„É´„Çø„É™„É≥„Ç∞
  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         task.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesPriority = filterPriority === 'all' || task.priority === filterPriority;
    return matchesSearch && matchesPriority;
  });

  // „Éá„Éº„Çø„Ç®„ÇØ„Çπ„Éù„Éº„Éà
  const handleExportData = () => {
    const exportData = {
      tasks: filteredTasks,
      columns,
      stats: projectStats,
      exportDate: new Date().toISOString(),
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json',
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `kanban-export-${projectId}-${Date.now()}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    addNotification({
      type: 'success',
      title: '„Éá„Éº„Çø„Çí„Ç®„ÇØ„Çπ„Éù„Éº„Éà„Åó„Åæ„Åó„Åü',
      message: '„Éï„Ç°„Ç§„É´„ÅÆ„ÉÄ„Ç¶„É≥„É≠„Éº„Éâ„ÇíÈñãÂßã„Åó„Åæ„Åó„Åü',
    });
  };

  if (isLoading) {
    return (
      <div className="kanban-container p-6">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">„Ç´„É≥„Éê„É≥„Éú„Éº„Éâ„ÇíË™≠„ÅøËæº„Åø‰∏≠...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="kanban-container p-6">
        <div className="text-center">
          <p className="text-red-600 mb-4">{error}</p>
          <button
            onClick={loadKanbanData}
            className="btn btn-primary"
          >
            ÂÜçË©¶Ë°å
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`kanban-container ${isMobileView ? 'mobile-view' : ''}`}>
      <div className="max-w-7xl mx-auto p-4">
        {/* „Éò„ÉÉ„ÉÄ„Éº */}
        <div className="mb-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4 mb-6">
            <div>
              <h1 className="text-3xl font-bold text-gray-800 mb-2">
                üöÄ BizFlow „Ç´„É≥„Éê„É≥„Éú„Éº„Éâ
              </h1>
              <p className="text-gray-600">
                „Éó„É≠„Ç∏„Çß„ÇØ„Éà: {projectId === 'default' ? 'ÂÖ®‰Ωì' : projectId}
                {isOffline && <span className="text-yellow-600 ml-2">(„Ç™„Éï„É©„Ç§„É≥)</span>}
              </p>
            </div>

            {/* „ÉÑ„Éº„É´„Éê„Éº */}
            <div className="flex flex-wrap gap-2">
              <button
                onClick={handleAddColumn}
                className="btn btn-primary"
                disabled={isOffline}
              >
                <Plus size={20} />
                <span className="hidden sm:inline ml-2">„Ç´„É©„É†ËøΩÂä†</span>
              </button>
              <button
                onClick={handleExportData}
                className="btn btn-secondary"
              >
                <Download size={20} />
                <span className="hidden sm:inline ml-2">„Ç®„ÇØ„Çπ„Éù„Éº„Éà</span>
              </button>
              <button className="btn btn-secondary">
                <BarChart3 size={20} />
                <span className="hidden sm:inline ml-2">ÂàÜÊûê</span>
              </button>
              <button className="btn btn-secondary">
                <Settings size={20} />
                <span className="hidden sm:inline ml-2">Ë®≠ÂÆö</span>
              </button>
            </div>
          </div>

          {/* „Éï„Ç£„É´„Çø„Éº„Å®Ê§úÁ¥¢ */}
          <div className="flex flex-col sm:flex-row gap-4 mb-6">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
                <input
                  type="text"
                  placeholder="„Çø„Çπ„ÇØ„ÇíÊ§úÁ¥¢..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="form-input pl-10"
                />
              </div>
            </div>
            <div className="flex gap-2">
              <select
                value={filterPriority}
                onChange={(e) => setFilterPriority(e.target.value)}
                className="form-input"
              >
                <option value="all">ÂÖ®„Å¶„ÅÆÂÑ™ÂÖàÂ∫¶</option>
                <option value="high">È´ò</option>
                <option value="medium">‰∏≠</option>
                <option value="low">‰Ωé</option>
              </select>
            </div>
          </div>

          {/* Áµ±Ë®àÊÉÖÂ†± */}
          {projectStats && (
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-6">
              <div className="card p-4 text-center">
                <div className="text-2xl font-bold text-blue-600 mb-1">
                  {projectStats.totalTasks}
                </div>
                <div className="text-sm text-gray-600">Á∑è„Çø„Çπ„ÇØÊï∞</div>
              </div>
              <div className="card p-4 text-center">
                <div className="text-2xl font-bold text-green-600 mb-1">
                  {projectStats.completionRate.toFixed(1)}%
                </div>
                <div className="text-sm text-gray-600">ÂÆå‰∫ÜÁéá</div>
              </div>
              <div className="card p-4 text-center">
                <div className="text-2xl font-bold text-orange-600 mb-1">
                  {projectStats.inProgressTasks}
                </div>
                <div className="text-sm text-gray-600">ÈÄ≤Ë°å‰∏≠</div>
              </div>
              <div className="card p-4 text-center">
                <div className="text-2xl font-bold text-purple-600 mb-1">
                  {projectStats.todoTasks}
                </div>
                <div className="text-sm text-gray-600">ÂæÖÊ©ü‰∏≠</div>
              </div>
            </div>
          )}
        </div>

        {/* „Ç´„É≥„Éê„É≥„Éú„Éº„Éâ */}
        <DndContext
          sensors={sensors}
          collisionDetection={closestCorners}
          onDragStart={handleDragStart}
          onDragOver={handleDragOver}
          onDragEnd={handleDragEnd}
        >
          <div className={`grid gap-6 ${
            isMobileView 
              ? 'grid-cols-1' 
              : 'grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4'
          }`}>
            {columns
              .sort((a, b) => a.order - b.order)
              .map(column => {
                const columnTasks = filteredTasks.filter(task => task.columnId === column.id);

                return (
                  <SortableContext
                    key={column.id}
                    items={column.taskIds}
                    strategy={verticalListSortingStrategy}
                  >
                    <Column
                      column={column}
                      tasks={columnTasks}
                      onAddTask={() => handleAddTask(column.id)}
                      onEditTask={handleEditTask}
                      onDeleteTask={handleDeleteTask}
                      isOffline={isOffline}
                    />
                  </SortableContext>
                );
              })}
          </div>

          {/* „Éâ„É©„ÉÉ„Ç∞„Ç™„Éº„Éê„Éº„É¨„Ç§ */}
          <DragOverlay>
            {activeTask ? (
              <TaskCard
                task={activeTask}
                onEdit={() => {}}
                onDelete={() => {}}
                isDragging
              />
            ) : null}
          </DragOverlay>
        </DndContext>

        {/* „Çø„Çπ„ÇØ‰ΩúÊàê„ÉªÁ∑®ÈõÜ„É¢„Éº„ÉÄ„É´ */}
        <TaskModal
          isOpen={isTaskModalOpen}
          onClose={() => {
            setIsTaskModalOpen(false);
            setEditingTask(null);
          }}
          onSave={handleSaveTask}
          task={editingTask}
          columns={columns}
        />

        {/* ÈÄöÁü•„Ç∑„Çπ„ÉÜ„É† */}
        <NotificationSystem notifications={notifications} />
      </div>
    </div>
  );
};

export default KanbanBoard;
