import React, { useState, useEffect } from 'react';
import {
  DndContext,
  DragEndEvent,
  DragOverEvent,
  DragStartEvent,
  PointerSensor,
  useSensor,
  useSensors,
  closestCorners,
  DragOverlay,
} from '@dnd-kit/core';
import {
  SortableContext,
  horizontalListSortingStrategy,
  verticalListSortingStrategy,
} from '@dnd-kit/sortable';
import { Column } from './Column';
import { TaskCard } from './TaskCard';
import { TaskModal } from './TaskModal';
import { NotificationSystem } from './NotificationSystem';
import { Task, ColumnType, ProjectStats, TaskStatus } from '../types';
import { taskOperations, columnOperations, projectOperations } from '../firebase/config';
import { Plus, Settings, BarChart3, Filter, Search, Download, Grid3X3, List } from 'lucide-react';

interface KanbanBoardProps {
  projectId?: string;
  isMobileView?: boolean;
  isOffline?: boolean;
}

const KanbanBoard: React.FC<KanbanBoardProps> = ({
  projectId = 'default',
  isMobileView = false,
  isOffline = false,
}) => {
  // StateÁÆ°ÁêÜ
  const [columns, setColumns] = useState<ColumnType[]>([]);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [activeTask, setActiveTask] = useState<Task | null>(null);
  const [isTaskModalOpen, setIsTaskModalOpen] = useState(false);
  const [editingTask, setEditingTask] = useState<Task | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [projectStats, setProjectStats] = useState<ProjectStats | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterPriority, setFilterPriority] = useState<string>('all');
  const [notifications, setNotifications] = useState<any[]>([]);
  const [currentColumnId, setCurrentColumnId] = useState<string>('');
  const [viewMode, setViewMode] = useState<'board' | 'list'>('board');

  // „Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Çª„É≥„Çµ„Éº
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: {
        distance: 8,
      },
    })
  );

  // ÂàùÊúü„Éá„Éº„ÇøË™≠„ÅøËæº„Åø
  useEffect(() => {
    loadKanbanData();
  }, [projectId]);

  // „É™„Ç¢„É´„Çø„Ç§„É†„É™„Çπ„Éä„ÉºË®≠ÂÆö
  useEffect(() => {
    if (isOffline) return;

    const unsubscribeTasks = taskOperations.onTasksChange((updatedTasks) => {
      setTasks(updatedTasks);
      updateProjectStats(updatedTasks);
    });

    const unsubscribeColumns = columnOperations.onColumnsChange((updatedColumns) => {
      setColumns(updatedColumns);
    });

    return () => {
      unsubscribeTasks();
      unsubscribeColumns();
    };
  }, [isOffline]);

  const loadKanbanData = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const [loadedTasks, loadedColumns] = await Promise.all([
        projectId === 'default' 
          ? taskOperations.getAllTasks()
          : taskOperations.getTasksByProject(projectId),
        columnOperations.getAllColumns(),
      ]);

      setTasks(loadedTasks);
      setColumns(loadedColumns);

      // „Éó„É≠„Ç∏„Çß„ÇØ„ÉàÁµ±Ë®à„ÇíÊõ¥Êñ∞
      await updateProjectStats(loadedTasks);

    } catch (err) {
      console.error('„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      setError('„Éá„Éº„Çø„ÅÆË™≠„ÅøËæº„Åø„Å´Â§±Êïó„Åó„Åæ„Åó„Åü');
    } finally {
      setIsLoading(false);
    }
  };

  const updateProjectStats = async (currentTasks: Task[]) => {
    try {
      const stats = await projectOperations.getProjectStats(
        projectId === 'default' ? undefined : projectId
      );
      setProjectStats(stats);
    } catch (err) {
      console.error('Áµ±Ë®à„ÅÆÊõ¥Êñ∞„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
    }
  };

  const addNotification = (notification: {
    type: 'success' | 'error' | 'warning' | 'info';
    title: string;
    message: string;
  }) => {
    const newNotification = {
      id: Date.now().toString(),
      ...notification,
      timestamp: new Date(),
    };
    setNotifications(prev => [...prev, newNotification]);

    // 5ÁßíÂæå„Å´Ëá™ÂãïÂâäÈô§
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== newNotification.id));
    }, 5000);
  };

  // „Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Ç§„Éô„É≥„Éà
  const handleDragStart = (event: DragStartEvent) => {
    const { active } = event;
    const task = tasks.find(t => t.id === active.id);
    setActiveTask(task || null);
  };

  const handleDragOver = (event: DragOverEvent) => {
    const { active, over } = event;
    if (!over) return;

    const activeId = active.id as string;
    const overId = over.id as string;

    const activeTask = tasks.find(t => t.id === activeId);
    if (!activeTask) return;

    const overColumn = columns.find(c => c.id === overId);
    if (overColumn && activeTask.columnId !== overId) {
      setTasks(prev =>
        prev.map(task =>
          task.id === activeId
            ? { ...task, columnId: overId, updatedAt: new Date() }
            : task
        )
      );
    }
  };

  const handleDragEnd = async (event: DragEndEvent) => {
    const { active, over } = event;
    setActiveTask(null);

    if (!over) return;

    const activeId = active.id as string;
    const overId = over.id as string;

    const activeTask = tasks.find(t => t.id === activeId);
    if (!activeTask) return;

    const overColumn = columns.find(c => c.id === overId);
    if (overColumn && activeTask.columnId !== overId) {
      try {
        await taskOperations.moveTask(activeId, overId);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÁßªÂãï„Åó„Åæ„Åó„Åü',
          message: `„Äå${activeTask.title}„Äç„Çí„Äå${overColumn.title}„Äç„Å´ÁßªÂãï„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Çø„Çπ„ÇØ„ÅÆÁßªÂãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Çø„Çπ„ÇØ„ÅÆÁßªÂãï„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
        // „Ç®„É©„ÉºÊôÇ„ÅØÂÖÉ„ÅÆÁä∂ÊÖã„Å´Êàª„Åô
        loadKanbanData();
      }
    }
  };

  // „Çø„Çπ„ÇØÊìç‰Ωú
  const handleAddTask = (columnId: string) => {
    setCurrentColumnId(columnId);
    setEditingTask(null);
    setIsTaskModalOpen(true);
  };

  const handleEditTask = (task: Task) => {
    setEditingTask(task);
    setIsTaskModalOpen(true);
  };

  const handleSaveTask = async (taskData: Omit<Task, 'id' | 'createdAt' | 'updatedAt'>) => {
    try {
      const dataToSave = {
        ...taskData,
        columnId: currentColumnId || taskData.columnId,
      };

      if (editingTask) {
        await taskOperations.updateTask(editingTask.id, dataToSave);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÊõ¥Êñ∞„Åó„Åæ„Åó„Åü',
          message: `„Äå${dataToSave.title}„Äç„ÇíÊõ¥Êñ∞„Åó„Åæ„Åó„Åü`,
        });
      } else {
        const taskId = await taskOperations.addTask({
          ...dataToSave,
          projectId: projectId === 'default' ? undefined : projectId,
        });
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„Çí‰ΩúÊàê„Åó„Åæ„Åó„Åü',
          message: `„Äå${dataToSave.title}„Äç„Çí‰ΩúÊàê„Åó„Åæ„Åó„Åü`,
        });
      }

      setIsTaskModalOpen(false);
      setEditingTask(null);
      setCurrentColumnId('');
    } catch (err) {
      console.error('„Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
      addNotification({
        type: 'error',
        title: '„Ç®„É©„Éº',
        message: '„Çø„Çπ„ÇØ„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
      });
    }
  };

  const handleDeleteTask = async (taskId: string) => {
    const task = tasks.find(t => t.id === taskId);
    if (!task) return;

    if (window.confirm(`„Äå${task.title}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü`)) {
      try {
        await taskOperations.deleteTask(taskId);
        addNotification({
          type: 'success',
          title: '„Çø„Çπ„ÇØ„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü',
          message: `„Äå${task.title}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Çø„Çπ„ÇØ„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Çø„Çπ„ÇØ„ÅÆÂâäÈô§„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
      }
    }
  };

  const handleAddColumn = async () => {
    const title = window.prompt('Êñ∞„Åó„ÅÑ„Ç´„É©„É†Âêç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ:');
    if (title) {
      try {
        await columnOperations.addColumn({
          title,
          color: '#6b7280',
          order: columns.length,
          taskIds: [],
        });
        addNotification({
          type: 'success',
          title: '„Ç´„É©„É†„ÇíËøΩÂä†„Åó„Åæ„Åó„Åü',
          message: `„Äå${title}„Äç„Ç´„É©„É†„ÇíËøΩÂä†„Åó„Åæ„Åó„Åü`,
        });
      } catch (err) {
        console.error('„Ç´„É©„É†„ÅÆËøΩÂä†„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', err);
        addNotification({
          type: 'error',
          title: '„Ç®„É©„Éº',
          message: '„Ç´„É©„É†„ÅÆËøΩÂä†„Å´Â§±Êïó„Åó„Åæ„Åó„Åü',
        });
      }
    }
  };

  // „Éï„Ç£„É´„Çø„É™„É≥„Ç∞
  const filteredTasks = tasks.filter(task => {
    const matchesSearch = task.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         task.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesPriority = filterPriority === 'all' || task.priority === filterPriority;
    return matchesSearch && matchesPriority;
  });

  // „Éá„Éº„Çø„Ç®„ÇØ„Çπ„Éù„Éº„Éà
  const handleExportData = () => {
    const exportData = {
      tasks: filteredTasks,
      columns,
      stats: projectStats,
      exportDate: new Date().toISOString(),
    };

    const blob = new Blob([JSON.stringify(exportData, null, 2)], {
      type: 'application/json',
    });

    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `kanban-export-${projectId}-${Date.now()}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);

    addNotification({
      type: 'success',
      title: '„Éá„Éº„Çø„Çí„Ç®„ÇØ„Çπ„Éù„Éº„Éà„Åó„Åæ„Åó„Åü',
